INSERT INTO `ss_sys_log` VALUES ('15603', '10', 'admin', 'cms/master-model/update', '编辑问答列表,提交', 'POST', '{\"r\":\"cms/master-model/update\",\"id\":\"6\",\"mid\":\"7\"}', '{\"_csrf-backend\":\"YIS8GQ2G_o6ae-9tUe4wOJ-9l60FUUUBf4FS1BVqvb8C6Y1IPOyZvO1D2gk8n2lr_on62mEnMXEY2xqfOD7Q7Q==\",\"MasterModel\":{\"columnid\":\"85\",\"title\":\"卧室梳妆台可以对着床摆放？有哪些风水讲究？\",\"colorval\":\"\",\"boldval\":\"\",\"slug\":\"#\",\"flag\":\"\",\"picurl\":\"\",\"diyfield_ask_content\":\"<p><span style=\\\"color: rgb(102, 102, 102); font-family: -apple-system, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);\\\">在卧室中，除了床之外，梳妆台也是非常重要的家具，不仅可以帮助收纳化妆等用品，而且还可以起到一定的装饰效果，因此，备受人们的喜爱。不过，卧室梳妆台的摆放也是很有讲究的，从风水上来说，卧室梳妆台不宜正对着床摆放，这样半夜醒来时容易被镜子中的自己吓到，导致精神过于紧张，不利于提高居住者的睡眠质量。</span></p>\",\"orderid\":\"100\",\"posttime\":\"2019-01-13\",\"status\":\"1\"},\"picurl\":\"\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/71.0.3578.98 Safari/537.36', 'e399272a56f0e3e8c9e53cab30b455e11', '', '1555911601');
